mapper.select:

            SELECT * FROM task
            WHERE status = #{status}
            AND mgroup = #{mgroup}
            AND projectName = #{projectName}
            AND runTimes > 0
            AND id >= (
    	        (SELECT MAX(id) FROM task WHERE status = #{status}
									      AND mgroup = #{mgroup}
									      AND projectName = #{projectName}
									      AND runTimes > 0)
    	        -
    	        (SELECT MIN(id) FROM task WHERE status = #{status}
									      AND mgroup = #{mgroup}
									      AND projectName = #{projectName}
									      AND runTimes > 0)
	            ) * RAND()
	            +
	            (SELECT MIN(id) FROM task WHERE status = #{status}
								          AND mgroup = #{mgroup}
								          AND projectName = #{projectName}
								          AND runTimes > 0)
            LIMIT 1;

mapper.insert:

    SELECT LAST_INSERT_ID()

INSERT INTO task (status,taskContent,loopType,runTimes,mgroup,projectName,loopRunTimes)
VALUES (#{status},#{taskContent},#{loopType},#{runTimes},#{mgroup},#{projectName},#{loopRunTimes});

mapper.update:
UPDATE task
status=#{status}, taskContent=#{taskContent}, loopType=#{loopType}, runTimes=#{runTimes}, mgroup=#{mgroup}, projectName=#{projectName}, loopRunTimes=#{loopRunTimes},
WHERE id = #{id};

UPDATE task
status=#{status}, taskContent=#{taskContent}, loopType=#{loopType}, runTimes=#{runTimes}, mgroup=#{mgroup}, projectName=#{projectName}, loopRunTimes=#{loopRunTimes},
WHERE loopType = #{loopType};
